    WHEN OTHERS THEN
        ROLLBACK;
        -- Lỗi ORA-00001 (Unique constraint violated) sẽ tự động được xử lý nếu bạn bỏ qua bước kiểm tra v_count
        RAISE_APPLICATION_ERROR(-20002, 'Lỗi hệ thống khi tạo yêu cầu linh kiện: ' || SQLERRM);
