--  DDL for Procedure GET_AUDIT_STATUS
--------------------------------------------------------
set define off;

  CREATE OR REPLACE EDITIONABLE PROCEDURE "APP"."GET_AUDIT_STATUS" (
    p_trigger_enabled  OUT NUMBER,
    p_standard_enabled OUT NUMBER,
    p_fga_enabled      OUT NUMBER
  ) AS
    v_trigger_total    NUMBER := 0;
    v_trigger_enabled  NUMBER := 0;
    v_standard_tables  NUMBER := 0;
    v_fga_policies     NUMBER := 0;
    v_expected_objects CONSTANT NUMBER := 18;
  BEGIN
    -- Trigger audit status
    SELECT COUNT(*) INTO v_trigger_total
      FROM USER_TRIGGERS
     WHERE TRIGGER_NAME LIKE 'TRG_AUDIT_%';

    SELECT COUNT(*) INTO v_trigger_enabled
      FROM USER_TRIGGERS
     WHERE TRIGGER_NAME LIKE 'TRG_AUDIT_%'
       AND STATUS = 'ENABLED';

    IF v_trigger_total > 0 AND v_trigger_total = v_trigger_enabled THEN
      p_trigger_enabled := 1;
    ELSE
      p_trigger_enabled := 0;
    END IF;

    -- Standard audit status (AUDIT INSERT/UPDATE/DELETE)
    SELECT COUNT(DISTINCT object_name) INTO v_standard_tables
      FROM DBA_OBJ_AUDIT_OPTS
     WHERE owner = USER
       AND object_name IN (
            'EMPLOYEE','CUSTOMER','ORDERS','STOCK_IN','STOCK_IN_ITEM',
            'PART','STOCK_OUT','STOCK_OUT_ITEM','PART_REQUEST','PART_REQUEST_ITEM',
            'USER_OTP_LOG','EMPLOYEE_SHIFT','CUSTOMER_APPOINTMENT','INVOICE',
            'INVOICE_ITEM','SERVICE','ORDER_SERVICE','INVOICE_SERVICE'
       );

    IF v_standard_tables = v_expected_objects THEN
      p_standard_enabled := 1;
    ELSE
      p_standard_enabled := 0;
    END IF;

    -- FGA audit policies
    SELECT COUNT(*) INTO v_fga_policies
      FROM DBA_AUDIT_POLICIES
     WHERE policy_owner = USER
       AND object_name IN (
            'EMPLOYEE','CUSTOMER','ORDERS','STOCK_IN','STOCK_IN_ITEM',
            'PART','STOCK_OUT','STOCK_OUT_ITEM','PART_REQUEST','PART_REQUEST_ITEM',
            'USER_OTP_LOG','EMPLOYEE_SHIFT','CUSTOMER_APPOINTMENT','INVOICE',
            'INVOICE_ITEM','SERVICE','ORDER_SERVICE','INVOICE_SERVICE'
       )
       AND enabled = 'YES';

    IF v_fga_policies = v_expected_objects THEN
      p_fga_enabled := 1;
    ELSE
      p_fga_enabled := 0;
    END IF;
  END get_audit_status;


/

